import React, { useContext } from 'react';

//import link
import { Link } from 'react-router-dom';

//import icons

import { BsPlusSquareFill, TfiWordpress } from 'react-icons/bs'


const Product = ({product}) => {
  console.log(product);

  //destructure product
  const { image } = product;
  return (

        // Add "Add to cart to every single square //
  <div>
    <div className='border h-[300px] mb-4 relative overflow-hidden group transition'>
      <div className='w-full h-full flex justify-center items-center'>
      {/* image */}
      <div className='w-[150px] mx-auto flex justify-center items-center'>
        <img 
        className='max-h-[170px] group-hover:scale 110 transition duration-300'
        src={image} 
        alt="" 
        />
      </div>
      </div>
      {/* buttons */}
      <div className='absolute top-0 right-0 bg-gray-200 p-2'>
        <buttons>
          <div className='flex justify-center items-center text-gray w-7 h-7'>
            <BsPlusSquareFill/>
          </div>
        </buttons>
        <Link to={'/'} className='w-7 h-7 bg-gray flex justify-center items-center text-primary drop-shadow-xl'>
        <TfiWordpress />
        </Link>
        </div>
  </div>
<div>2</div>
</div>
);
};

export default Product;
