  // destructure item
  const {id, title, image, price, amount } = item;
  return(
  <div className='flex'>
    <div className='w-full min-h-[150] flex items-center gap-x-4'>
    {/* {image} */}
    <Link to ={`/product/${id}`}>
    <img className='max-w-[80px]' src={image} alt='' />
    </Link>
    </div>
    </div>
    );
};

export default CartItem;
